insert.Script:
(function() {
    // Function to update product quantity
    function updateProductQuantity(orderID) {
        var sqlText = 'SELECT ProductID, Quantity FROM OrderDetail WHERE OrderID = @OrderID';
        var parameters = { '@OrderID': orderID };

        // Execute SQL to fetch order details
        $app.execute({
            sql: sqlText,
            parameters: parameters,
            success: function(result) {
                var orderDetails = result.data;
                orderDetails.forEach(function(orderDetail) {
                    var productID = orderDetail.ProductID;
                    var orderedQuantity = orderDetail.Quantity;

                    // SQL to update product quantity
                    var updateSql = 'UPDATE Product SET Quantity = Quantity - @OrderedQuantity WHERE ProductID = @ProductID';
                    var updateParams = {
                        '@OrderedQuantity': orderedQuantity,
                        '@ProductID': productID
                    };

                    // Execute SQL to update product quantity
                    $app.execute({
                        sql: updateSql,
                        parameters: updateParams,
                        success: function() {
                            console.log('Product quantity updated successfully for ProductID:', productID);
                        },
                        error: function(error) {
                            console.error('Error updating product quantity for ProductID:', productID, error);
                        }
                    });
                });
            },
            error: function(error) {
                console.error('Error fetching order details for OrderID:', orderID, error);
            }
        });
    }

    // Fetch the OrderID from the current context
    var orderID = $context.row.OrderID;

    // Call the function to update product quantity
    updateProductQuantity(orderID);
})();

