businessRules.rule:
var fieldValue = [NumericGradeEarned];
if (fieldValue != null && fieldValue > [extMaxScore]) {
    // prevent the default action processing
    this.preventDefault();
    // set the focus on the field and display an error
    this.result.focus('NumericGradeEarned', 'Score earned cannot be greater than Threshold.');
}
